import Header from "./Header";
import { withRouter } from "react-router-dom";
import { useState, useEffect } from "react";

function UpdateProducts(props) {
  const [name, setName] = useState("");
  const [file, setFile] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");

  console.warn("props", props.match.params.id);
  const [data, setData] = useState([]);

  useEffect(async () => {
    let result = await fetch(
      "http://localhost:8000/api/product/" + props.match.params.id
    );
    result = await result.json();
    setData(result);
    setName(data.name);
    setPrice(data.price);
    setDescription(data.description);
    setFile(data.file);
  }, []);

  function editProduct(id) {
    alert(id);
  }

  return (
    <div>
      <Header />
      <h1>Update Products</h1>
      <div className="col-sm-6 offset-sm-3">
        <br />
        <input
          className="form-control"
          type="text"
          defaultValue={data.name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          className="form-control"
          type="text"
          defaultValue={data.price}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          className="form-control"
          type="text"
          defaultValue={data.description}
        />
        <input
          className="form-control"
          type="file"
          defaultValue={data.file_path}
        />
        <img
          width="50%"
          height="50%"
          src={"http://localhost:8000/" + data.file_path}
        />{" "}
        <br />
        <button className="btn btn-danger" onClick={() => editProduct(data.id)}>
          Update Product
        </button>
      </div>
    </div>
  );
}
export default withRouter(UpdateProducts);
