import Header from "./Header";
import { withRouter } from "react-router-dom";
import { useState, useEffect } from "react";

function UpdateProducts(props) {
  const [data, setData] = useState([]);

  console.warn("props", props.match.params.id);
  useEffect(async () => {
    let result = await fetch(
      "http://localhost:8000/api/product/" + props.match.params.id
    );
    result = await result.json();
    setData(result);
  });
  return (
    <div>
      <Header />
      <h1>Update Products</h1>
      <div className="col-sm-6 offset-sm-3">
        <input type="text" className="form-control" defaultValue={data.name} />{" "}
        <br />
        <br />
        <input
          type="text"
          className="form-control"
          defaultValue={data.price}
        />{" "}
        <br />
        <br />
        <input
          type="text"
          className="form-control"
          defaultValue={data.description}
        />{" "}
        <br />
        <br />
        <input
          type="file"
          className="form-control"
          defaultValue={data.file_path}
        />{" "}
        <br />
        <br />
        <img
          style={{ width: "40%", height: "40%" }}
          src={"http://localhost:8000/" + data.file_path}
        />{" "}
        <br />
        <br />
        <button>Update Product</button>
      </div>
    </div>
  );
}
export default withRouter(UpdateProducts);
