import Header from "./Header";
import { withRouter } from "react-router-dom";
import { useState, useEffect } from "react";

function UpdateProducts(props) {
  const [data, setData] = useState([]);
  const [name, setName] = useState("");
  const [file, setFile] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");

  // console.warn("props", props.match.params.id);
  useEffect(async () => {
    let result = await fetch(
      "http://localhost:8000/api/product/" + props.match.params.id
    );
    result = await result.json();
    setData(result);
  });

  async function updateProduct() {
    //  console.warn(name, file, price, description);
    const formData = new FormData();
    formData.append("file", file);
    formData.append("price", price);
    formData.append("name", name);
    formData.append("description", description);
    let result = await fetch(
      "http://localhost:8000/api/addProduct" + props.match.params.id,
      {
        method: "POST",
        body: formData,
      }
    );
    alert("Product added!");
  }

  return (
    <div>
      <Header />
      <h1>Update Products</h1>
      <input
        type="text"
        className="form-control"
        defaultValue={data.name}
        onChange={(e) => setName(e.target.value)}
      />{" "}
      <br />
      <br />
      <input
        type="text"
        className="form-control"
        defaultValue={data.price}
        onChange={(e) => setPrice(e.target.value)}
      />{" "}
      <br />
      <br />
      <input
        type="text"
        className="form-control"
        defaultValue={data.description}
        onChange={(e) => setDescription(e.target.value)}
      />{" "}
      <br />
      <br />
      <input
        type="file"
        className="form-control"
        defaultValue={data.file_path}
        onChange={(e) => setFile(e.target.files[0])}
      />{" "}
      <br />
      <br />
      <img
        style={{ width: "40%", height: "40%" }}
        src={"http://localhost:8000/" + data.file_path}
      />{" "}
      <br />
      <br />
      <button className="btn btn-danger" onClick={updateProduct}>
        Update Product
      </button>
    </div>
  );
}
export default withRouter(UpdateProducts);
