import Header from "./Header";
import { withRouter } from "react-router-dom";
import { useState, useEffect } from "react";

function UpdateProducts(props) {
  console.warn("props", props.match.params.id);
  const [data, setData] = useState([]);

  useEffect(async () => {
    let result = await fetch(
      "http://localhost:8000/api/product/" + props.match.params.id
    );
    result = await result.json();
    setData(result);
  }, []);

  return (
    <div>
      <Header />
      <h1>Update Products</h1>
      <div className="col-sm-6 offset-sm-3">
        <br />
        <input className="form-control" type="text" defaultValue={data.name} />
        <input className="form-control" type="text" defaultValue={data.price} />
        <input
          className="form-control"
          type="text"
          defaultValue={data.description}
        />
        <input
          className="form-control"
          type="file"
          defaultValue={data.file_path}
        />{" "}
        <img src={"http://localhost:8000/" + data.file_path} /> <br />
        <button className="btn btn-danger">Update Product</button>
      </div>
    </div>
  );
}
export default withRouter(UpdateProducts);
